import React from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";

const Menu = () => {
  const menuItems = [
    { id: 1, name: "Espresso", price: "$3.50", description: "Strong black coffee" },
    { id: 2, name: "Cappuccino", price: "$4.50", description: "Espresso with steamed milk" },
    { id: 3, name: "Latte", price: "$5.00", description: "Espresso with lots of milk" },
    { id: 4, name: "Mocha", price: "$5.50", description: "Chocolate flavored latte" },
    { id: 5, name: "Americano", price: "$4.00", description: "Espresso with hot water" },
    { id: 6, name: "Macchiato", price: "$4.50", description: "Espresso with a dollop of milk" },
  ];

  return (
    <div className="min-h-screen font-sans flex flex-col">
      <Header />
      
      <main className="flex-1 bg-[#f5f5f5] py-12 px-6 sm:px-10">
        <div className="max-w-6xl mx-auto">
          <h1 className="text-3xl sm:text-4xl font-bold text-[#4a2600] mb-8 text-center">OUR MENU</h1>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {menuItems.map((item) => (
              <div key={item.id} className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow">
                <h2 className="text-xl font-bold text-[#8a4b27]">{item.name}</h2>
                <p className="text-gray-600 mt-2">{item.description}</p>
                <p className="text-[#4a2600] font-bold mt-4">{item.price}</p>
              </div>
            ))}
          </div>
        </div>
      </main>
      
      <Footer />
    </div>
  );
};

export default Menu;
