import React from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";

const Menu = () => {
  const menuCategories = [
    {
      id: 1,
      name: "Cold Coffee",
      items: [
        { id: 11, name: "Iced Americano", price: "$4.00" },
        { id: 12, name: "Cold Brew", price: "$4.50" },
        { id: 13, name: "Iced Latte", price: "$5.00" },
      ]
    },
    {
      id: 2,
      name: "Chocolate",
      items: [
        { id: 21, name: "Hot Chocolate", price: "$4.50" },
        { id: 22, name: "Mocha", price: "$5.00" },
        { id: 23, name: "White Chocolate", price: "$5.50" },
      ]
    },
    {
      id: 3,
      name: "Americano",
      items: [
        { id: 31, name: "Classic Americano", price: "$3.50" },
        { id: 32, name: "Long Black", price: "$4.00" },
      ]
    },
    {
      id: 4,
      name: "Masala Chai",
      items: [
        { id: 41, name: "Traditional Masala Chai", price: "$3.50" },
        { id: 42, name: "Cardamom Chai", price: "$4.00" },
        { id: 43, name: "Ginger Chai", price: "$4.00" },
      ]
    }
  ];

  return (
    <div 
      className="min-h-screen font-sans flex flex-col bg-fixed"
      style={{
        backgroundImage: "url('/path-to-your-background-image.jpg')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat"
      }}
    >
      <div className="bg-black bg-opacity-50 flex flex-col flex-1">
        <Header />
        
        <main className="flex-1 py-12 px-6 sm:px-10">
          <div className="max-w-6xl mx-auto">
            <h1 className="text-4xl sm:text-5xl font-bold text-white mb-12 text-center uppercase tracking-wider">
              Our Menu
            </h1>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              {menuCategories.map((category) => (
                <div key={category.id} className="bg-white bg-opacity-90 rounded-lg shadow-lg overflow-hidden">
                  <div className="bg-[#8a4b27] p-4">
                    <h2 className="text-2xl font-bold text-white text-center uppercase tracking-wider">
                      {category.name}
                    </h2>
                  </div>
                  
                  <div className="p-6">
                    {category.items.map((item) => (
                      <div key={item.id} className="mb-6 last:mb-0 pb-6 border-b border-[#f0f0f0] last:border-0">
                        <div className="flex justify-between items-start">
                          <h3 className="text-lg font-bold text-[#4a2600]">{item.name}</h3>
                          <span className="text-[#8a4b27] font-bold">{item.price}</span>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </main>
        
        <Footer />
      </div>
    </div>
  );
};

export default Menu;
