import React from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";

const Services = () => {
  // Updated services to match your image
  const services = [
    { id: 1, title: "Dine-In Service" },
    { id: 2, title: "Takeaway/Parcel" },
    { id: 3, title: "Online Ordering" },
    { id: 4, title: "Custom Coffee Orders" },
    { id: 5, title: "Snacks & Bakery Items" },
    { id: 6, title: "Free Wi-Fi" },
    { id: 7, title: "Loyalty Program" },
    { id: 8, title: "Events/Live Music" },
    { id: 9, title: "Seasonal Specials" },
    { id: 10, title: "Gift Cards/Merchandise" }
  ];

  return (
    <div className="min-h-screen font-sans flex flex-col">
      <Header />
      
      <main className="flex-1 py-12 px-6 sm:px-10 bg-[#fff9f5]">
        <div className="max-w-6xl mx-auto">
          {/* Updated heading to match image style */}
          <h1 className="text-4xl sm:text-5xl font-bold text-[#4a2600] mb-12 text-center uppercase tracking-wider">
            Our Services
          </h1>
          
          {/* Services grid - 2 columns on mobile, 3 on tablet, 4 on desktop */}
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {services.map((service) => (
              <div 
                key={service.id} 
                className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow"
              >
                <div className="flex items-center">
                  {/* Number badge */}
                  <div className="bg-[#8a4b27] text-white w-8 h-8 rounded-full flex items-center justify-center mr-4">
                    <span className="font-bold">{service.id}</span>
                  </div>
                  {/* Service title */}
                  <h2 className="text-lg font-bold text-[#4a2600]">
                    {service.title}
                  </h2>
                </div>
              </div>
            ))}
          </div>

          {/* Optional description section */}
          <div className="mt-16 bg-white p-8 rounded-lg shadow-md">
            <h2 className="text-2xl font-bold text-[#8a4b27] mb-4">About Our Services</h2>
            <p className="text-gray-600">
              We offer a wide range of services to enhance your coffee experience. 
              From convenient takeaway options to special events, we have something 
              for every coffee lover. Enjoy our free Wi-Fi while you work or relax, 
              or join our loyalty program to earn rewards with every purchase.
            </p>
          </div>
        </div>
      </main>
      
      <Footer />
    </div>
  );
};

export default Services;
