// pages/EditProfile.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';

const EditProfile = () => {
    const [user, setUser] = useState({
        name: "John Doe",
        email: "john@example.com",
        favoriteDrink: "Masala Chai",
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUser(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Here you would typically send the updated data to your backend
        alert('Profile updated successfully!');
    };

    return (
        <div className="min-h-screen flex flex-col">
            <Header />
            <main className="flex-grow container mx-auto px-4 py-8">
                <div className="max-w-2xl mx-auto bg-white rounded-lg shadow-md overflow-hidden">
                    <div className="bg-amber-800 p-6 text-white">
                        <h1 className="text-3xl font-bold">Edit Profile</h1>
                    </div>
                    
                    <div className="p-6">
                        <form onSubmit={handleSubmit}>
                            <div className="mb-4">
                                <label htmlFor="name" className="block text-gray-700 font-medium mb-2">
                                    Full Name
                                </label>
                                <input
                                    type="text"
                                    id="name"
                                    name="name"
                                    value={user.name}
                                    onChange={handleChange}
                                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-amber-500"
                                    required
                                />
                            </div>
                            
                            <div className="mb-4">
                                <label htmlFor="email" className="block text-gray-700 font-medium mb-2">
                                    Email
                                </label>
                                <input
                                    type="email"
                                    id="email"
                                    name="email"
                                    value={user.email}
                                    onChange={handleChange}
                                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-amber-500"
                                    required
                                />
                            </div>
                            
                            <div className="mb-6">
                                <label htmlFor="favoriteDrink" className="block text-gray-700 font-medium mb-2">
                                    Favorite Drink
                                </label>
                                <select
                                    id="favoriteDrink"
                                    name="favoriteDrink"
                                    value={user.favoriteDrink}
                                    onChange={handleChange}
                                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-amber-500"
                                >
                                    <option value="Masala Chai">Masala Chai</option>
                                    <option value="Mocha">Mocha</option>
                                    <option value="Espresso">Espresso</option>
                                    <option value="Cappuccino">Cappuccino</option>
                                    <option value="Latte">Latte</option>
                                </select>
                            </div>
                            
                            <div className="flex justify-between">
                                <Link 
                                    to="/profile" 
                                    className="bg-gray-300 hover:bg-gray-400 text-gray-800 px-4 py-2 rounded-md transition"
                                >
                                    Cancel
                                </Link>
                                <button
                                    type="submit"
                                    className="bg-amber-600 hover:bg-amber-700 text-white px-4 py-2 rounded-md transition"
                                >
                                    Save Changes
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </main>
            <Footer />
        </div>
    );
};

export default EditProfile;
