import { useCart } from '../context/CartContext';

const Menu = () => {
    const { addToCart } = useCart();

    // Sample coffee data - you can replace with your actual data
    const coffeeItems = [
        {
            id: 1,
            name: "Americano",
            price: 3.50,
            description: "Rich espresso with hot water",
            image: "americano.jpg"
        },
        {
            id: 2,
            name: "Cold Coffee",
            price: 4.00,
            description: "Iced coffee with milk and sweetener",
            image: "cold-coffee.jpg"
        },
        {
            id: 3,
            name: "Masala Chai",
            price: 3.00,
            description: "Spiced Indian tea with milk",
            image: "masala-chai.jpg"
        },
        {
            id: 4,
            name: "Hot Chocolate",
            price: 4.50,
            description: "Creamy chocolate drink",
            image: "chocolate.jpg"
        }
    ];

    return (
        <div className="menu-page">
            <h1>Our Menu</h1>
            <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
                gap: '20px',
                padding: '20px'
            }}>
                {coffeeItems.map(item => (
                    <div key={item.id} style={{
                        border: '1px solid #ddd',
                        borderRadius: '8px',
                        padding: '15px',
                        boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
                    }}>
                        <img 
                            src={`/assets/${item.image}`} 
                            alt={item.name}
                            style={{
                                width: '100%',
                                height: '200px',
                                objectFit: 'cover',
                                borderRadius: '5px'
                            }}
                        />
                        <h3>{item.name}</h3>
                        <p>{item.description}</p>
                        <div style={{
                            display: 'flex',
                            justifyContent: 'space-between',
                            alignItems: 'center'
                        }}>
                            <span>${item.price.toFixed(2)}</span>
                            <button 
                                onClick={() => addToCart(item)}
                                style={{
                                    background: '#6F4E37',
                                    color: 'white',
                                    border: 'none',
                                    padding: '8px 15px',
                                    borderRadius: '4px',
                                    cursor: 'pointer'
                                }}
                            >
                                Add to Cart
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Menu;
