import React from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";

const Services = () => {
  const services = [
    { 
      title: "Coffee Catering", 
      description: "We provide professional coffee catering services for your events and meetings.",
      icon: "â˜•"
    },
    { 
      title: "Barista Training", 
      description: "Learn the art of coffee making from our professional baristas.",
      icon: "ðŸŽ“"
    },
    { 
      title: "Coffee Subscription", 
      description: "Get fresh coffee beans delivered to your door every month.",
      icon: "ðŸ“¦"
    },
  ];

  return (
    <div className="min-h-screen font-sans flex flex-col">
      <Header />
      
      <main className="flex-1 py-12 px-6 sm:px-10 bg-[#fff9f5]">
        <div className="max-w-6xl mx-auto">
          <h1 className="text-3xl sm:text-4xl font-bold text-[#4a2600] mb-12 text-center">OUR SERVICES</h1>
          
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {services.map((service, index) => (
              <div key={index} className="bg-white p-8 rounded-lg shadow-md text-center">
                <div className="text-4xl mb-4">{service.icon}</div>
                <h2 className="text-xl font-bold text-[#8a4b27] mb-3">{service.title}</h2>
                <p className="text-gray-600">{service.description}</p>
              </div>
            ))}
          </div>
        </div>
      </main>
      
      <Footer />
    </div>
  );
};

export default Services;
