import React from "react";

const Services = () => {
  const services = [
    { id: 1, name: "Dine-In Service" },
    { id: 2, name: "Takeaway/Parcel" },
    { id: 3, name: "Online Ordering" },
    { id: 4, name: "Custom Coffee Orders" },
    { id: 5, name: "Snacks & Bakery Items" },
    { id: 6, name: "Free Wi-Fi" },
    { id: 7, name: "Loyalty Program" },
    { id: 8, name: "Events/Live Music" },
    { id: 9, name: "Seasonal Specials" },
    { id: 10, name: "Gift Cards/Merchandise" }
  ];

  return (
    <div className="min-h-[calc(100vh-80px)] p-10 bg-amber-50">
      <div className="max-w-4xl mx-auto">
        {/* Services Header */}
        <h1 className="text-4xl font-bold text-[#4a2600] mb-10 text-center">SERVICES</h1>
        
        {/* Services Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {services.map((service) => (
            <div 
              key={service.id}
              className="bg-white p-6 rounded-lg shadow-md flex items-start hover:shadow-lg transition-shadow"
            >
              <span className="text-2xl font-bold text-[#8a4b27] mr-4">{service.id}.</span>
              <p className="text-lg text-[#4a2600] font-medium">{service.name}</p>
            </div>
          ))}
        </div>

        {/* Additional Info Section */}
        <div className="mt-12 text-center">
          <p className="text-[#4a2600] font-medium">
            Ask our staff about special service packages and seasonal offers!
          </p>
        </div>
      </div>
    </div>
  );
};

export default Services;
