import { useCart } from '../context/CartContext';
import CartItem from '../components/CartItem';

const Cart = () => {
    const { 
        cartItems, 
        cartTotal, 
        removeFromCart, 
        updateQuantity, 
        clearCart,
        isCartOpen,
        setIsCartOpen
    } = useCart();

    if (!isCartOpen) return null;

    return (
        <div style={{
            position: 'fixed',
            top: 0,
            right: 0,
            width: '350px',
            height: '100vh',
            background: 'white',
            boxShadow: '-2px 0 5px rgba(0,0,0,0.1)',
            zIndex: 999,
            padding: '20px',
            overflowY: 'auto'
        }}>
            <div style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                marginBottom: '20px'
            }}>
                <h2>Your Coffee Cart</h2>
                <button onClick={() => setIsCartOpen(false)}>Close</button>
            </div>

            {cartItems.length === 0 ? (
                <p>Your cart is empty</p>
            ) : (
                <>
                    {cartItems.map(item => (
                        <CartItem 
                            key={item.id}
                            item={item}
                            onRemove={removeFromCart}
                            onUpdateQuantity={updateQuantity}
                        />
                    ))}
                    <div style={{
                        marginTop: '20px',
                        borderTop: '1px solid #ddd',
                        paddingTop: '20px'
                    }}>
                        <h3>Total: ${cartTotal.toFixed(2)}</h3>
                        <button 
                            onClick={clearCart}
                            style={{
                                background: '#ff4444',
                                color: 'white',
                                border: 'none',
                                padding: '8px 15px',
                                marginRight: '10px',
                                borderRadius: '4px',
                                cursor: 'pointer'
                            }}
                        >
                            Clear Cart
                        </button>
                        <button 
                            style={{
                                background: '#6F4E37',
                                color: 'white',
                                border: 'none',
                                padding: '8px 15px',
                                borderRadius: '4px',
                                cursor: 'pointer'
                            }}
                        >
                            Checkout
                        </button>
                    </div>
                </>
            )}
        </div>
    );
};

export default Cart;
