import React from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import coffeeBg from "../assets/coffee-bg.jpg";
import peopleImage from "../assets/about-people.jpg"; // Add this image of two people

const About = () => {
  return (
    <div className="min-h-screen font-sans flex flex-col">
      <Header />
      
      <main className="flex-1 relative">
        {/* Background with overlay */}
        <div className="absolute inset-0 overflow-hidden">
          <img 
            src={coffeeBg} 
            alt="Coffee background"
            className="w-full h-full object-cover brightness-[0.7] scale-110" 
          />
          <div className="absolute inset-0 bg-black bg-opacity-30"></div>
        </div>
        
        {/* Content container */}
        <div className="relative min-h-[calc(100vh-80px)] flex items-center p-6 sm:p-10">
          <div className="max-w-6xl mx-auto w-full flex flex-col lg:flex-row items-center">
            {/* Left Side - Content */}
            <div className="lg:w-1/2 text-white mb-10 lg:mb-0 lg:pr-10">
              <div className="bg-black bg-opacity-70 p-8 rounded-xl backdrop-blur-sm">
                <h1 className="text-4xl sm:text-5xl font-bold mb-6 text-center uppercase">ABOUT</h1>
                
                <div className="text-lg sm:text-xl leading-relaxed space-y-4">
                  <p>
                    Welcome to COFFEE HOUSE – your cozy corner for handcrafted coffee, comfy seating, 
                    and warm conversations. We serve ethically sourced, fresh-roasted beans brewed with love.
                  </p>
                  <p>
                    Whether you need a morning boost or a peaceful evening sip, our café is your perfect stop.
                  </p>
                </div>
              </div>
            </div>
            
            {/* Right Side - People Image */}
            <div className="lg:w-1/2 flex justify-center">
              <div className="max-w-md w-full rounded-xl overflow-hidden shadow-2xl border-4 border-amber-100 border-opacity-30">
                <img 
                  src={peopleImage} 
                  alt="Two people enjoying coffee"
                  className="w-full h-auto object-cover"
                />
              </div>
            </div>
          </div>
        </div>
      </main>
      
      <Footer />
    </div>
  );
};

export default About;
